// compar pixelul central cu un vecin folosind threshold
__device__ __forceinline__ int diffType(const int v, const int x, const int th)
{
    const int diff = x - v;

    return static_cast<int>(diff < -th) + (static_cast<int>(diff > th) << 1);
}

__device__ void calcMask(const uint C[4], const int v, const int th, int& mask1, int& mask2)
{
    mask1 = 0; // bitii sunt 1 daca vecinul e mai inchis
    mask2 = 0; // bitii sunt 1 daca vecinul e mai deschis

    int d1, d2;

    d1 = diffType(v, C[0] & 0xff, th);
    d2 = diffType(v, C[2] & 0xff, th);

    if ((d1 | d2) == 0)
        return;

    mask1 |= (d1 & 1) << 0; mask2 |= ((d1 & 2) >> 1) << 0;
    mask1 |= (d2 & 1) << 8; mask2 |= ((d2 & 2) >> 1) << 8;

    d1 = diffType(v, C[1] & 0xff, th);
    d2 = diffType(v, C[3] & 0xff, th);

    if ((d1 | d2) == 0)
        return;

    mask1 |= (d1 & 1) << 4; mask2 |= ((d1 & 2) >> 1) << 4;
    mask1 |= (d2 & 1) << 12;mask2 |= ((d2 & 2) >> 1) << 12;

    d1 = diffType(v, (C[0] >> (2 * 8)) & 0xff, th);
    d2 = diffType(v, (C[2] >> (2 * 8)) & 0xff, th);

    if ((d1 | d2) == 0)
        return;

    mask1 |= (d1 & 1) << 2; mask2 |= ((d1 & 2) >> 1) << 2;
    mask1 |= (d2 & 1) << 10; mask2 |= ((d2 & 2) >> 1) << 10;

    d1 = diffType(v, (C[1] >> (2 * 8)) & 0xff, th);
    d2 = diffType(v, (C[3] >> (2 * 8)) & 0xff, th);

    if ((d1 | d2) == 0)
        return;

    mask1 |= (d1 & 1) << 6; mask2 |= ((d1 & 2) >> 1) << 6;
    mask1 |= (d2 & 1) << 14; mask2 |= ((d2 & 2) >> 1) << 14;

    d1 = diffType(v, (C[0] >> (1 * 8)) & 0xff, th);
    d2 = diffType(v, (C[2] >> (1 * 8)) & 0xff, th);

    mask1 |= (d1 & 1) << 1; mask2 |= ((d1 & 2) >> 1) << 1;
    mask1 |= (d2 & 1) << 9; mask2 |= ((d2 & 2) >> 1) << 9;

    d1 = diffType(v, (C[0] >> (3 * 8)) & 0xff, th);
    d2 = diffType(v, (C[2] >> (3 * 8)) & 0xff, th);

    mask1 |= (d1 & 1) << 3; mask2 |= ((d1 & 2) >> 1) << 3;
    mask1 |= (d2 & 1) << 11; mask2 |= ((d2 & 2) >> 1) << 11;

    d1 = diffType(v, (C[1] >> (1 * 8)) & 0xff, th);
    d2 = diffType(v, (C[3] >> (1 * 8)) & 0xff, th);

    mask1 |= (d1 & 1) << 5; mask2 |= ((d1 & 2) >> 1) << 5;
    mask1 |= (d2 & 1) << 13; mask2 |= ((d2 & 2) >> 1) << 13;

    d1 = diffType(v, (C[1] >> (3 * 8)) & 0xff, th);
    d2 = diffType(v, (C[3] >> (3 * 8)) & 0xff, th);

    mask1 |= (d1 & 1) << 7; mask2 |= ((d1 & 2) >> 1) << 7;
    mask1 |= (d2 & 1) << 15; mask2 |= ((d2 & 2) >> 1) << 15;
}

__device__ __forceinline__ bool isKeyPoint(int mask1, int mask2)
{
    return (__popc(mask1) > 8 && (c_table[(mask1 >> 3) - 63] & (1 << (mask1 & 7)))) ||
           (__popc(mask2) > 8 && (c_table[(mask2 >> 3) - 63] & (1 << (mask2 & 7))));
}

__device__ int cornerScore(const uint C[4], const int v, const int threshold)
{
    // binary search in [threshold + 1, 255]

    int min = threshold + 1;
    int max = 255;

    while (min <= max)
    {
        const int mid = (min + max) >> 1;

        int mask1 = 0;
        int mask2 = 0;

        calcMask(C, v, mid, mask1, mask2);

        int isKp = static_cast<int>(isKeyPoint(mask1, mask2));

        min = isKp * (mid + 1) + (isKp ^ 1) * min;
        max = (isKp ^ 1) * (mid - 1) + isKp * max;
    }

    return min - 1;
}

__device__ int isKeyPoint2(const uchar* img, const int i, const int j, const int threshold, int step) {
    int v;
    uint C[4] = {0,0,0,0};

    C[2] |= static_cast<uint>(img[index(i - 3, j - 1, step)]) << 8;
    C[2] |= static_cast<uint>(img[index(i - 3, j, step)]);
    C[1] |= static_cast<uint>(img[index(i - 3, j + 1, step)]) << (3 * 8);

    C[2] |= static_cast<uint>(img[index(i - 2, j - 2, step)]) << (2 * 8);
    C[1] |= static_cast<uint>(img[index(i - 2, j + 2, step)]) << (2 * 8);

    C[2] |= static_cast<uint>(img[index(i - 1, j - 3, step)]) << (3 * 8);
    C[1] |= static_cast<uint>(img[index(i - 1, j + 3, step)]) << 8;

    C[3] |= static_cast<uint>(img[index(i, j - 3, step)]);
    v     = static_cast<int>(img[index(i, j, step)]);
    C[1] |= static_cast<uint>(img[index(i, j + 3, step)]);

    int d1 = diffType(v, C[1] & 0xff, threshold);
    int d2 = diffType(v, C[3] & 0xff, threshold);

    if ((d1 | d2) == 0) {
        return 0;
    }
    C[3] |= static_cast<uint>(img[index(i + 1, j - 3, step)]) << 8;
    C[0] |= static_cast<uint>(img[index(i + 1, j + 3, step)]) << (3 * 8);

    C[3] |= static_cast<uint>(img[index(i + 2, j - 2, step)]) << (2 * 8);
    C[0] |= static_cast<uint>(img[index(i + 2, j + 2, step)]) << (2 * 8);

    C[3] |= static_cast<uint>(img[index(i + 3, j - 1, step)]) << (3 * 8);
    C[0] |= static_cast<uint>(img[index(i + 3, j, step)]);
    C[0] |= static_cast<uint>(img[index(i + 3, j + 1, step)]) << 8;

    int mask1 = 0;
    int mask2 = 0;

    calcMask(C, v, threshold, mask1, mask2);
    if (isKeyPoint(mask1, mask2)) {
        return cornerScore(C, v, threshold);
    } else {
        return 0;
    }
}

__device__ bool isMax(int x, int y, int step, uint8_t *scoreMat) {
    int score = scoreMat[index(x, y, step)];
    bool ismax =
            score > scoreMat[index(x - 1, y - 1, step)] &&
            score > scoreMat[index(x    , y - 1, step)] &&
            score > scoreMat[index(x + 1, y - 1, step)] &&

            score > scoreMat[index(x - 1, y    , step)] &&
            score > scoreMat[index(x + 1, y    , step)] &&

            score > scoreMat[index(x - 1, y + 1, step)] &&
            score > scoreMat[index(x    , y + 1, step)] &&
            score > scoreMat[index(x + 1, y + 1, step)];
    return ismax;
}

__global__
void fast_corner(uchar *images, uchar *inputImage, uint8_t *d_Rs, ORB_SLAM3::GpuPoint *buffers, 
    int rows, int cols, uint8_t t_h, uint8_t t_low, uint *sizes, float *mvScaleFactor, int maxLevel, int inputImageStep){

    const int tid = threadIdx.y * blockDim.x + threadIdx.x;
    const int level = blockIdx.z * blockDim.z + threadIdx.z;

    const float scale = mvScaleFactor[level];
    const int new_rows = round(rows * 1/scale);
    const int new_cols = round(cols * 1/scale);
    const int minBorderX = EDGE_THRESHOLD-3;
    const int minBorderY = minBorderX;
    const int maxBorderX = new_cols-EDGE_THRESHOLD+3;
    const int maxBorderY = new_rows-EDGE_THRESHOLD+3;

    const int x = blockIdx.x * blockDim.x + threadIdx.x + minBorderX + 3;
    const int y = (blockIdx.y * blockDim.y + threadIdx.y) * 4 + minBorderY + 3;

    const int h = maxBorderY - minBorderY;
    const int w = maxBorderX - minBorderX;

    const int imageStep = (level == 0) * inputImageStep + (level != 0) * new_cols;

    const uchar* im[2] = {inputImage, &(images[(level*cols*rows)])};
    const int imIndex = (level != 0);

    const uchar *image = im[imIndex];

    uint *size = &(sizes[level]);
    ORB_SLAM3::GpuPoint *buffer = &(buffers[level*cols*rows]);

    const uint maxKeypoints = w*h;

    __shared__ bool hasKp;
    if (tid == 0) {
        hasKp = false;
    }

    bool isKp[4] = {0};
    for (int t = 0; t < 4; ++t) {
        if (y+t < maxBorderY - 3 && x < maxBorderX - 3 && level < maxLevel) {
            const int score = isKeyPoint2(image, x, y+t, t_h, imageStep);
            isKp[t] = score;
            d_Rs[index(x, y+t, imageStep)] = score;
        }
    }
    // barrieer
    __syncthreads();
    for (int t = 0; t < 4; ++t) {
        if (isKp[t]) {
            isKp[t] = false;
            if (isMax(x, y+t, imageStep, d_Rs)) {
                int score = d_Rs[index(x, y+t, imageStep)];
                hasKp = true;
                const unsigned int ind = atomicInc(size, maxKeypoints);
                buffer[ind].x = x;
                buffer[ind].y = y+t;
                buffer[ind].score = score;
            }
        }
    }
    // barrieer
    __syncthreads();
    if (hasKp) return ;

    // lower the threshold and try again
    for (int t = 0; t < 4; ++t) {
        if (y+t < maxBorderY - 3 && x < maxBorderX - 3 && y+t > minBorderY && x > minBorderX && level < maxLevel) {
            const int score = isKeyPoint2(image, x, y+t, t_low, imageStep);
            isKp[t] = score;
            d_Rs[index(x, y+t, imageStep)] = score;
        }
    }
    // barrieer
    __syncthreads();
    for (int t = 0; t < 4; ++t) {
        if (isKp[t]) {
            if (isMax(x, y+t, imageStep, d_Rs)) {
                int score = d_Rs[index(x, y+t, imageStep)];
                const unsigned int ind = atomicInc(size, maxKeypoints);
                buffer[ind].x = x;
                buffer[ind].y = y+t;
                buffer[ind].score = score;
            }
        }
    }
}

void fast_extract( uchar *images, uchar *inputImage, uint8_t th, uint8_t th_low, uint8_t *d_Rs, uint8_t *d_Rs_low, int *points, 
    int n_, ORB_SLAM3::GpuPoint *buffers, uint *sizes, int cols, int rows, int inputImageStep, float* mvScaleFactor, int maxLevel, 
    cudaStream_t cudaStream, cudaEvent_t interComplete, cv::Mat pyramid){

    dim3 dimBlock(32, 8, 1);
    dim3 dimGrid(ceil((float)cols/dimBlock.x), ceil((float)rows/(dimBlock.y * 4)), maxLevel);
    cudaMemsetAsync(sizes, 0, sizeof(uint)*maxLevel, cudaStream);
    fast_corner<<<dimGrid, dimBlock, 0, cudaStream>>>(images, inputImage, d_Rs, buffers, rows, cols, th, th_low, 
        sizes, mvScaleFactor, maxLevel, inputImageStep);
    cudaEventRecord(interComplete, cudaStream);
}